/*
 * 9. Introduce el número de un mes del año por teclado, indica el nombre del mes.
 */
import java.util.Scanner;

public class ProblemasPropuestos9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner teclado=new Scanner(System.in);
		int A;
		System.out.println("Escoja un mes");
		A=teclado.nextInt();
		if (A==1){
			System.out.println("El mes escogido es Enero");
		}
		else if (A==2){
			System.out.println("El mes escogido es Febrero");
		}
		else if (A==3){
			System.out.println("El mes escogido es Marzo");
		}
		else if ( A==4){
			System.out.println("El mes escogido es Abril");
		}
		else if (A==5){
			System.out.println("El mes escogido es Mayo");
		}
		else if (A==6){
			System.out.println("El mes escogido es Junio");
		}
		else if (A==7){
			System.out.println("El mes escogido es Julio");
		}
		else if (A==8){
			System.out.println("El mes escogido es Agosto");
		}else if (A==9){
			System.out.println("El mes escogido es Septiembre");
		}else if (A==10){
			System.out.println("El mes escogido es Octubre");
		}else if (A==11){
			System.out.println("El mes escogido es Noviembre");
		}else if (A==12){
			System.out.println("El mes escogido es Diciembre");
		}
		}
}	
		
		
	
	


